import{_ as e,o,c as s,k as t}from"./chunks/framework.UluYd6x4.js";const $=JSON.parse('{"title":"献身","description":"","frontmatter":{"title":"献身","date":"2023-06-07T22:18:48.000Z","permalink":"/pages/38ef42/","sidebar":"auto","categories":["随笔"],"tags":[null]},"headers":[],"relativePath":"know/献身.md","filePath":"know/献身.md","lastUpdated":1704104554000}'),a={name:"know/献身.md"},n=t("p",null,"一、人的本质",-1),_=t("p",null,"一、向上的渴望 回想之前的读书经历，当我刚开始接触到有关思考的一些的书籍时，时常有这样的情况，我受到了头晕目眩的冲击，我大三时读了一本书，是尼采的《查拉图斯特拉如是说》，当然是老师推荐的书。我也说不上是怎么回事，尼采思想的那种近乎癫狂的驱动力，还有查拉图斯特仿佛魔力一般能引起情绪起伏的诉说。总之，那本书有 着模糊虚实界限的能力。我全然分不清我在哪儿，我到底是谁，时间似乎静止了，我钻到了那本书里。我觉得我不是在读它，而是被包裹在那散文般的激流中，猛然的思想冲刷着我。感觉我自己的思想在溶解，灵魂出窍，被人施了魔咒，如果有天堂的话那就是了，我就在了。虽然之后我时常可以体验到超验的感觉。但当时对我来说， 我既有的框架被打碎了，我不得不在痛苦中重塑。我并不是尼采的粉丝，甚至于他的思想有些地方与我相背，但这并不妨碍我去理解他的世界，他们的世界，这些曾经的天才们时至今日依然还能带我去领略那更高的殿堂。我一直想的是， 只要我还一直读书，我就一直能理解自己的痛苦，一直与自己的无知、狭隘、偏见、阴暗见招拆招。我并不期待人生可以过得很顺利，但我希望碰到人生难关的时候。自己可以是它的对手。",-1),l=t("p",null,"二、智慧的渴求",-1),c=t("p",null,"三、真诚的实践",-1),r=t("p",null,"四、",-1),d=[n,_,l,c,r];function i(p,u,h,m,f,k){return o(),s("div",null,d)}const w=e(a,[["render",i]]);export{$ as __pageData,w as default};
